import React from 'react';
import { Button } from '@consta/uikit/Button';
import { IconEdit } from '@consta/icons/IconEdit';
import { IconTrash } from '@consta/icons/IconTrash';
import { Table, TableColumn, TableRow } from '@consta/uikit/Table';

interface Customer {
  id: number;
  customerCode: string;
  customerName: string;
  customerInn: string;
  customerKpp: string;
  customerLegalAddress: string;
  customerPostalAddress: string;
  customerEmail: string;
  customerCodeMain: string;
  isOrganization: boolean;
  isPerson: boolean;
}

interface CustomerRow extends Omit<Customer, 'id'>, TableRow {
  id: string;
}

interface Props {
  customers: Customer[];
}

const columns: TableColumn<CustomerRow>[] = [
  {
    title: 'Код',
    accessor: 'customer_code',
  },
  {
    title: 'Наименование',
    accessor: 'customer_name',
  },
  {
    title: 'ИНН',
    accessor: 'customer_inn',
  },
  {
    title: 'КПП',
    accessor: 'customer_kpp',
  },
  {
    title: 'Юр. адрес',
    accessor: 'customer_legal_address',
  },
  {
    title: 'Почтовый адрес',
    accessor: 'customer_postal_address',
  },
  {
    title: 'Электронная почта',
    accessor: 'customer_email',
  },
  {
    title: 'Действия',
    renderCell: (customer: CustomerRow) => (
      <>
        <Button
          iconLeft={IconEdit}
          size="s"
          view="ghost"
          onClick={() => console.log(`Изменить ${customer.customerCode}`)}
          style={{ marginRight: '8px' }}
        />
        <Button
          iconLeft={IconTrash}
          size="s"
          view="ghost"
          onClick={() => console.log(`Удалить ${customer.customerCode}`)}
        />
      </>
    ),
  } as any,
];

const CustomerList: React.FC<Props> = ({ customers }) => {
  console.log('Customers:', customers); // Логирование полученных данных

  const rows: CustomerRow[] = customers.map((customer) => {
    if (customer.id === undefined) {
      console.error('Customer ID is undefined for customer:', customer);
      return {
        ...customer,
        id: 'undefined', // или любое другое значение по умолчанию
      };
    }

    return {
      ...customer,
      id: customer.id.toString(),
    };
  });

  return (
    <div>
      <Table columns={columns} rows={rows} />
    </div>
  );
};

export default CustomerList;
