import React, { useEffect, useState } from 'react';
import { Table, TableColumn, TextField } from '@consta/uikit/Table';
import { Button } from '@consta/uikit/Button';
import { IconEdit } from '@consta/icons/IconEdit';
import { IconTrash } from '@consta/icons/IconTrash';

interface Customer {
  customer_code: string;
  customer_name: string;
  customer_inn: string;
  customer_kpp: string;
  customer_legal_address: string;
  customer_postal_address: string;
  customer_email: string;
  customer_code_main: string;
  is_organization: boolean;
  is_person: boolean;
}

const columns: TableColumn<Customer>[] = [
  {
    title: 'Код',
    accessor: 'customer_code',
  },
  {
    title: 'Наименование',
    accessor: 'customer_name',
  },
  {
    title: 'ИНН',
    accessor: 'customer_inn',
  },
  {
    title: 'КПП',
    accessor: 'customer_kpp',
  },
  {
    title: 'Юр. адрес',
    accessor: 'customer_legal_address',
  },
  {
    title: 'Почтовый адрес',
    accessor: 'customer_postal_address',
  },
  {
    title: 'Электронная почта',
    accessor: 'customer_email',
  },
  {
    title: 'Действия',
    accessor: 'actions',
    renderCell: (customer, onEditCustomer) => (
      <>
        <Button
          iconLeft={IconEdit}
          size="s"
          view="ghost"
          onClick={() => onEditCustomer(customer)}
          style={{ marginRight: '8px' }}
        />
        <Button
          iconLeft={IconTrash}
          size="s"
          view="ghost"
          onClick={() => console.log(`Удалить ${customer.customer_code}`)}
        />
      </>
    ),
  },
];

const CustomerList: React.FC<{
  onEditCustomer: (customer: Customer) => void;
}> = ({ onEditCustomer }) => {
  const [customers, setCustomers] = useState<Customer[]>([]);
  const [filter, setFilter] = useState<string>('');

  useEffect(() => {
    getCustomers().then((data) => setCustomers(data));
  }, []);

  const filteredCustomers = customers.filter((customer) =>
    customer.customer_name.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div>
      <TextField
        value={filter}
        onChange={({ value }) => setFilter(value ?? '')}
        placeholder="Фильтр по наименованию"
      />
      <Table
        columns={columns}
        rows={filteredCustomers}
        renderCell={(row, column) =>
          column.renderCell
            ? column.renderCell(row, onEditCustomer)
            : row[column.accessor]
        }
      />
    </div>
  );
};

export default CustomerList;
