import React from 'react';
import { Text } from '@consta/uikit/Text';
import { Button } from '@consta/uikit/Button';
import { IconEdit } from '@consta/Icons/IconEdit';
import { IconTrash } from '@consta/Icons/IconTrash';

interface Customer {
  customer_code: string;
  customer_name: string;
  customer_inn: string;
  customer_kpp: string;
  customer_legal_address: string;
  customer_postal_address: string;
  customer_email: string;
  customer_code_main: string;
  is_organization: boolean;
  is_person: boolean;
}

const customers: Customer[] = [
  {
    customer_code: '001',
    customer_name: 'ООО "Ромашка"',
    customer_inn: '1234567890',
    customer_kpp: '123456789',
    customer_legal_address: 'г. Москва, ул. Ленина, д. 1',
    customer_postal_address: 'г. Москва, ул. Ленина, д. 1',
    customer_email: 'romashka@example.com',
    customer_code_main: '000',
    is_organization: true,
    is_person: false,
  },
  {
    customer_code: '002',
    customer_name: 'ИП Иванов И.И.',
    customer_inn: '0987654321',
    customer_kpp: '987654321',
    customer_legal_address: 'г. Санкт-Петербург, ул. Пушкина, д. 2',
    customer_postal_address: 'г. Санкт-Петербург, ул. Пушкина, д. 2',
    customer_email: 'ivanov@example.com',
    customer_code_main: '001',
    is_organization: false,
    is_person: true,
  },
  // Добавьте больше клиентов по необходимости
];

const CustomerList: React.FC = () => {
  return (
    <div>
      {customers.map((customer) => (
        <div
          key={customer.customer_code}
          className="customer-item"
          style={{ marginBottom: '16px' }}
        >
          <Text as="p">
            <strong>Код:</strong> {customer.customer_code}
            <br />
            <strong>Наименование:</strong> {customer.customer_name}
            <br />
            <strong>ИНН:</strong> {customer.customer_inn}
            <br />
            <strong>КПП:</strong> {customer.customer_kpp}
            <br />
            <strong>Юр. адрес:</strong> {customer.customer_legal_address}
            <br />
            <strong>Почтовый адрес:</strong> {customer.customer_postal_address}
            <br />
            <strong>Электронная почта:</strong> {customer.customer_email}
            <br />
            <strong>Вышестоящий контрагент:</strong>{' '}
            {customer.customer_code_main}
            <br />
            <strong>Юр. лицо:</strong> {customer.is_organization ? 'Да' : 'Нет'}
            <br />
            <strong>Физ. лицо:</strong> {customer.is_person ? 'Да' : 'Нет'}
          </Text>
          <Button
            iconLeft={IconEdit}
            label="Изменить"
            onClick={() => console.log(`Изменить ${customer.customer_code}`)}
            style={{ marginRight: '8px' }}
          />
          <Button
            iconLeft={IconTrash}
            label="Удалить"
            onClick={() => console.log(`Удалить ${customer.customer_code}`)}
          />
          <hr />
        </div>
      ))}
    </div>
  );
};

export default CustomerList;
