import React, { useState } from 'react';
import { Text } from '@consta/uikit/Text';
import { Button } from '@consta/uikit/Button';
import AddCustomerModal from './AddCustomerModal';
import UpdateCustomerModal from './UpdateCustomerModal';
import CustomerList from './CustomerList';

function Customer() {
  const [isAddModalOpen, setIsAddModalOpen] = useState<boolean>(false);
  const [isUpdateModalOpen, setIsUpdateModalOpen] = useState<boolean>(false);
  const [selectedCustomer, setSelectedCustomer] = useState<any>(null);

  const handleCloseAddModal = () => {
    setIsAddModalOpen(false);
  };

  const handleCloseUpdateModal = () => {
    setIsUpdateModalOpen(false);
    setSelectedCustomer(null);
  };

  const handleOpenUpdateModal = (customer: any) => {
    setSelectedCustomer(customer);
    setIsUpdateModalOpen(true);
  };

  const handleSaveCustomer = (updatedCustomer: any) => {
    // Implement the logic to update the customer in your state or API
    console.log('Updated Customer:', updatedCustomer);
  };

  return (
    <div className="container">
      <div className="instrument-header">
        <div>
          <Text as="h1">Управление заказчиками</Text>
          <Button label="Добавление" onClick={() => setIsAddModalOpen(true)} />
        </div>
      </div>

      <Text as="p" lineHeight="m">
        В этом окне вы можете управлять заказчиками, добавлять и удалять их, а
        также вносить необходимые изменения. Благодаря удобному интерфейсу,
        процесс управления клиентами является максимально простым и эффективным.
        При необходимости вы всегда сможете быстро обновить информацию,
        обеспечивая точность и актуальность данных.
      </Text>

      <hr />

      <CustomerList onEditCustomer={handleOpenUpdateModal} />

      <AddCustomerModal isOpen={isAddModalOpen} onClose={handleCloseAddModal} />
      {selectedCustomer && (
        <UpdateCustomerModal
          isOpen={isUpdateModalOpen}
          onClose={handleCloseUpdateModal}
          customerData={selectedCustomer}
          onSave={handleSaveCustomer}
        />
      )}
    </div>
  );
}

export default Customer;
