import React from 'react';
import { Text } from '@consta/uikit/Text';
import { Button } from '@consta/uikit/Button';
import { Select } from '@consta/uikit/Select';
import CustomerList from './CustomerList';
import { Customer } from '../../intefaces/CustomerInteface';

type FilterOption = {
  label: string;
  value: 'all' | 'hasClient' | 'noClient';
};

interface Props {
  customers: Customer[];
  handleDeleteCustomer: (id: number) => void;
  openEditModal: (customer: Customer) => void;
  openAddModal: () => void;
  setFilter: (filter: 'all' | 'hasClient' | 'noClient') => void;
  filter: 'all' | 'hasClient' | 'noClient';
}

const filterOptions: FilterOption[] = [
  { label: 'Все', value: 'all' },
  { label: 'С контрагентом', value: 'hasClient' },
  { label: 'Без контрагента', value: 'noClient' },
];

const CustomerForm: React.FC<Props> = ({
  customers,
  handleDeleteCustomer,
  openEditModal,
  openAddModal,
  setFilter,
  filter,
}) => {
  const handleSelectChange = (option: FilterOption | null) => {
    if (option) {
      setFilter(option.value);
    }
  };

  // Находим текущее выбранное значение из опций
  const selectedFilter =
    filterOptions.find((option) => option.value === filter) || null;

  return (
    <div className="container">
      <div className="instrument-header">
        <div>
          <Text as="h1">Управление заказчиками</Text>
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <Select
              value={selectedFilter}
              items={filterOptions}
              onChange={handleSelectChange}
              getItemKey={(item) => item.value}
              getItemLabel={(item) => item.label}
              placeholder="Фильтр"
            />
            <Button label="Добавить" onClick={openAddModal} />
          </div>
        </div>
      </div>
      <Text as="p" lineHeight="m">
        В этом окне вы можете управлять заказчиками, добавлять и удалять их, а
        также вносить необходимые изменения. Благодаря удобному интерфейсу,
        процесс управления клиентами является максимально простым и эффективным.
      </Text>
      <hr />
      <CustomerList
        customers={customers}
        handleDeleteCustomer={handleDeleteCustomer}
        openEditModal={openEditModal}
      />
    </div>
  );
};

export default CustomerForm;
