import React from 'react';
import { Button } from '@consta/uikit/Button';
import { IconEdit } from '@consta/icons/IconEdit';
import { IconTrash } from '@consta/icons/IconTrash';
import { Table, TableColumn, TableRow } from '@consta/uikit/Table';

interface Customer {
  id: number;
  customerCode: string;
  customerName: string;
  customerInn: string;
  customerKpp: string;
  customerLegalAddress: string;
  customerPostalAddress: string;
  customerEmail: string;
  customerCodeMain: string | null;
  isOrganization: boolean;
  isPerson: boolean;
}

interface CustomerRow extends Omit<Customer, 'id'>, TableRow {
  id: string;
}

interface Props {
  customers: Customer[];
  handleDeleteCustomer: (id: number) => void;
}

const columns: TableColumn<CustomerRow>[] = [
  {
    title: 'Действия',
    renderCell: (customer: CustomerRow) => (
      <>
        <Button
          iconLeft={IconEdit}
          size="s"
          view="ghost"
          onClick={() => console.log(`Изменить ${customer.id}`)}
          style={{ marginRight: '8px' }}
        />
        <Button iconLeft={IconTrash} size="s" view="ghost" />
      </>
    ),
  } as any,
];

const CustomerList: React.FC<Props> = ({ customers, handleDeleteCustomer }) => {
  const rows: CustomerRow[] = customers.map((customer) => ({
    ...customer,
    id: customer.id.toString(),
  }));

  return (
    <div>
      <Table columns={columns} rows={rows} />
    </div>
  );
};

export default CustomerList;
