import React, { useState } from 'react';
import { Modal } from '@consta/uikit/Modal';
import { TextField } from '@consta/uikit/TextField';
import { Button } from '@consta/uikit/Button';
import { Grid, GridItem } from '@consta/uikit/Grid';
import { Select } from '@consta/uikit/Select';
import { Text } from '@consta/uikit/Text';

interface Customer {
  id: number;
  customerCode: string;
  customerName: string;
  customerInn: string;
  customerKpp: string;
  customerLegalAddress: string;
  customerPostalAddress: string;
  customerEmail: string;
  customerCodeMain: string;
  isOrganization: boolean;
  isPerson: boolean;
}

type Props = {
  isOpen: boolean;
  onClose: () => void;
  handleSaveCustomer: (customer: Customer) => Promise<void>;
};

const customerTypeOptions: { label: string; value: string }[] = [
  { label: 'Юридическое лицо', value: 'organization' },
  { label: 'Физическое лицо', value: 'person' },
];

const AddCustomerModal: React.FC<Props> = ({
  isOpen,
  onClose,
  handleSaveCustomer,
}) => {
  const [customer, setCustomer] = useState<Omit<Customer, 'id'>>({
    customerCode: '',
    customerName: '',
    customerInn: '',
    customerKpp: '',
    customerLegalAddress: '',
    customerPostalAddress: '',
    customerEmail: '',
    customerCodeMain: '',
    isOrganization: true,
    isPerson: false,
  });

  const [errors, setErrors] = useState<Record<string, string>>({});

  const handleInputChange =
    (field: keyof Omit<Customer, 'id'>) => (value: string | null) => {
      setCustomer((prev) => ({
        ...prev,
        [field]: value ?? '',
      }));
    };

  const handleSelectChange = (
    option: { label: string; value: string } | null
  ) => {
    setCustomer((prev) => ({
      ...prev,
      isOrganization: option?.value === 'organization',
      isPerson: option?.value === 'person',
    }));
  };

  const validateFields = (): boolean => {
    const newErrors: Record<string, string> = {};
    if (!customer.customerCode)
      newErrors.customerCode = 'Код контрагента обязателен';
    if (!customer.customerName)
      newErrors.customerName = 'Наименование обязательно';
    if (!customer.customerInn) newErrors.customerInn = 'ИНН обязателен';
    if (!customer.customerKpp) newErrors.customerKpp = 'КПП обязателен';
    if (!customer.customerLegalAddress)
      newErrors.customerLegalAddress = 'Юридический адрес обязателен';
    if (!customer.customerPostalAddress)
      newErrors.customerPostalAddress = 'Почтовый адрес обязателен';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async () => {
    if (validateFields()) {
      try {
        const newCustomer = { id: 0, ...customer };
        await handleSaveCustomer(newCustomer);
        onClose(); // Закрытие модального окна после успешного сохранения
      } catch (err) {
        setErrors({
          submit:
            'Ошибка при добавлении заказчика. Проверьте корректность данных.',
        });
      }
    }
  };

  return (
    <Modal isOpen={isOpen} onClose={onClose} style={{ maxWidth: '800px' }}>
      <div style={{ padding: '20px' }}>
        <h3>Добавление нового заказчика</h3>
        {errors.submit && (
          <div style={{ marginBottom: '20px', color: 'red' }}>
            <Text>{errors.submit}</Text>
          </div>
        )}
        <Grid cols={3} gap="m">
          <GridItem>
            <TextField
              label="Код контрагента"
              value={customer.customerCode}
              onChange={handleInputChange('customerCode')}
              placeholder="Введите код контрагента"
            />
          </GridItem>
          <GridItem>
            <TextField
              label="Наименование"
              value={customer.customerName}
              onChange={handleInputChange('customerName')}
              placeholder="Введите наименование"
            />
          </GridItem>
          <GridItem>
            <TextField
              label="ИНН"
              value={customer.customerInn}
              onChange={handleInputChange('customerInn')}
              placeholder="Введите ИНН"
            />
          </GridItem>
          <GridItem>
            <TextField
              label="КПП"
              value={customer.customerKpp}
              onChange={handleInputChange('customerKpp')}
              placeholder="Введите КПП"
            />
          </GridItem>
          <GridItem>
            <TextField
              label="Юридический адрес"
              value={customer.customerLegalAddress}
              onChange={handleInputChange('customerLegalAddress')}
              placeholder="Введите юридический адрес"
            />
          </GridItem>
          <GridItem>
            <TextField
              label="Почтовый адрес"
              value={customer.customerPostalAddress}
              onChange={handleInputChange('customerPostalAddress')}
              placeholder="Введите почтовый адрес"
            />
          </GridItem>
          <GridItem>
            <TextField
              label="Электронная почта"
              value={customer.customerEmail}
              onChange={handleInputChange('customerEmail')}
              placeholder="Введите электронную почту"
            />
          </GridItem>
          <GridItem>
            <TextField
              label="Вышестоящий контрагент"
              value={customer.customerCodeMain}
              onChange={handleInputChange('customerCodeMain')}
              placeholder="Введите код вышестоящего контрагента"
            />
          </GridItem>
          <GridItem>
            <Select
              label="Тип лица"
              placeholder="Выберите тип"
              items={customerTypeOptions}
              value={
                customerTypeOptions.find(
                  (option) =>
                    option.value ===
                    (customer.isOrganization ? 'organization' : 'person')
                ) || null
              }
              onChange={handleSelectChange}
              getItemKey={(item) => item.value}
            />
          </GridItem>
          <GridItem>
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <Button label="Добавить" onClick={handleSubmit} />
              <Button label="Отмена" onClick={onClose} />
            </div>
          </GridItem>
        </Grid>
      </div>
    </Modal>
  );
};

export default AddCustomerModal;
