import React, { useState } from 'react';
import { Modal } from '@consta/uikit/Modal';
import { TextField } from '@consta/uikit/TextField';
import { Button } from '@consta/uikit/Button';
import { Grid, GridItem } from '@consta/uikit/Grid';

const AddCustomerModal: React.FC<{ isOpen: boolean; onClose: () => void }> = ({
  isOpen,
  onClose,
}) => {
  const [clientName, setClientName] = useState<string>('');
  const [clientEmail, setClientEmail] = useState<string>('');

  const handleAddClient = () => {
    // Логика для добавления клиента
    console.log('Client added:', { name: clientName, email: clientEmail });
    onClose(); // Закрыть модальное окно после добавления
  };

  return (
    <Modal isOpen={isOpen} onClose={onClose}>
      <div style={{ padding: '20px' }}>
        <h3>Добавить нового клиента</h3>
        <Grid cols="1" gap="m">
          <GridItem>
            <TextField
              label="Имя клиента"
              value={clientName}
              onChange={({ value }) => setClientName(value)}
            />
          </GridItem>
          <GridItem>
            <TextField
              label="Email клиента"
              value={clientEmail}
              onChange={({ value }) => setClientEmail(value)}
            />
          </GridItem>
          <GridItem>
            <Button label="Добавить" onClick={handleAddClient} />
          </GridItem>
        </Grid>
      </div>
    </Modal>
  );
};

export default AddCustomerModal;
