import React, { useState } from 'react';
import { Button } from '@consta/uikit/Button';
import { IconEdit } from '@consta/icons/IconEdit';
import { IconTrash } from '@consta/icons/IconTrash';
import { Table, TableColumn, TableRow } from '@consta/uikit/Table';
import UpdateCustomerModal from './UpdateCustomerModal';

interface Customer {
  id: number;
  customerCode: string;
  customerName: string;
  customerInn: string;
  customerKpp: string;
  customerLegalAddress: string;
  customerPostalAddress: string;
  customerEmail: string;
  customerCodeMain: string | null;
  isOrganization: boolean;
  isPerson: boolean;
}

interface CustomerRow extends Omit<Customer, 'id'>, TableRow {
  id: string;
}

interface Props {
  customers: Customer[];
  handleDeleteCustomer: (id: number) => void;
  handleSaveCustomer: (savedCustomer: Customer) => void;
}

const CustomerList: React.FC<Props> = ({
  customers,
  handleDeleteCustomer,
  handleSaveCustomer,
}) => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [customerToEdit, setCustomerToEdit] = useState<Customer | null>(null);

  const columns: TableColumn<CustomerRow>[] = [
    {
      title: 'Код',
      accessor: 'customerCode',
    },
    {
      title: 'Наименование',
      accessor: 'customerName',
    },
    {
      title: 'ИНН',
      accessor: 'customerInn',
    },
    {
      title: 'КПП',
      accessor: 'customerKpp',
    },
    {
      title: 'Юр. адрес',
      accessor: 'customerLegalAddress',
    },
    {
      title: 'Почтовый адрес',
      accessor: 'customerPostalAddress',
    },
    {
      title: 'Электронная почта',
      accessor: 'customerEmail',
    },
    {
      title: 'Контрагент',
      accessor: 'customerCodeMain',
    },
    {
      title: 'Тип',
      renderCell: (customer: CustomerRow) => (
        <span>{customer.isOrganization ? 'Юр. лицо' : 'Физ. лицо'}</span>
      ),
    },
    {
      title: 'Действия',
      renderCell: (customer: CustomerRow) => (
        <>
          <Button
            iconLeft={IconEdit}
            size="s"
            view="ghost"
            onClick={() => {
              setCustomerToEdit(
                customers.find((c) => c.id.toString() === customer.id) || null
              );
              setModalOpen(true);
            }}
            style={{ marginRight: '8px' }}
          />
          <Button
            iconLeft={IconTrash}
            size="s"
            view="ghost"
            onClick={() => handleDeleteCustomer(Number(customer.id))}
          />
        </>
      ),
    } as any,
  ];

  const rows: CustomerRow[] = customers.map((customer) => ({
    ...customer,
    id: customer.id.toString(),
  }));

  const handleUpdateCustomer = async (updatedCustomer: Customer) => {
    // Предполагается, что handleSaveCustomer используется и для обновления, и для добавления
    await handleSaveCustomer(updatedCustomer);
    setModalOpen(false);
  };

  return (
    <div>
      <Table columns={columns} rows={rows} />
      {isModalOpen && customerToEdit && (
        <UpdateCustomerModal
          isOpen={isModalOpen}
          onClose={() => setModalOpen(false)}
          handleUpdateCustomer={handleUpdateCustomer}
          customerToEdit={customerToEdit}
        />
      )}
    </div>
  );
};

export default CustomerList;
