import React, { useState, useEffect } from 'react';
import { PurchaseApi } from '../../http/PurchaseApi';
import CustomerForm from './CustomerForm';

interface Customer {
  id: number;
  customerCode: string;
  customerName: string;
  customerInn: string;
  customerKpp: string;
  customerLegalAddress: string;
  customerPostalAddress: string;
  customerEmail: string;
  customerCodeMain: string;
  isOrganization: boolean;
  isPerson: boolean;
}

const customerTypeOptions = [
  { label: 'Юридическое лицо', value: 'organization' },
  { label: 'Физическое лицо', value: 'person' },
];

const CustomerPage: React.FC = () => {
  const [customers, setCustomers] = useState<Customer[]>([]);
  const [currentCustomer, setCurrentCustomer] = useState<Customer | null>(null);
  const [isModalOpen, setIsModalOpen] = useState<boolean>(false);

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = async () => {
    try {
      const response = await PurchaseApi.getCustomers();
      setCustomers(response.data);
    } catch (error) {
      console.error('Error fetching customers', error);
    }
  };

  const handleInputChange = (name: string, value: string) => {
    if (currentCustomer) {
      setCurrentCustomer({ ...currentCustomer, [name]: value });
    }
  };

  const handleSelectChange = (
    option: { label: string; value: string } | null
  ) => {
    if (currentCustomer && option) {
      setCurrentCustomer({
        ...currentCustomer,
        isOrganization: option.value === 'organization',
        isPerson: option.value === 'person',
      });
    }
  };

  const handleCreateCustomer = async () => {
    if (currentCustomer) {
      try {
        await PurchaseApi.createCustomer(currentCustomer);
        fetchCustomers();
        setIsModalOpen(false);
      } catch (error) {
        console.error('Error creating customer', error);
      }
    }
  };

  const handleEditCustomer = (customer: Customer) => {
    setCurrentCustomer(customer);
    setIsModalOpen(true);
  };

  const handleUpdateCustomer = async () => {
    if (currentCustomer && currentCustomer.id) {
      try {
        await PurchaseApi.updateCustomer(currentCustomer.id, currentCustomer);
        fetchCustomers();
        setIsModalOpen(false);
      } catch (error) {
        console.error('Error updating customer', error);
      }
    }
  };

  const handleDeleteCustomer = async (id: number) => {
    try {
      await PurchaseApi.deleteCustomer(id);
      fetchCustomers();
    } catch (error) {
      console.error('Error deleting customer', error);
    }
  };

  const openModal = () => {
    setIsModalOpen(true);
    setCurrentCustomer({
      id: 0,
      customerCode: '',
      customerName: '',
      customerInn: '',
      customerKpp: '',
      customerLegalAddress: '',
      customerPostalAddress: '',
      customerEmail: '',
      customerCodeMain: '',
      isOrganization: false,
      isPerson: false,
    });
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setCurrentCustomer(null);
  };

  return (
    <div>
      <CustomerForm
        customers={customers}
        handleDeleteCustomer={handleDeleteCustomer}
        handleCreateCustomer={handleCreateCustomer}
        handleInputChange={handleInputChange}
        isModalOpen={isModalOpen}
        openModal={openModal}
        closeModal={closeModal}
        currentCustomer={currentCustomer}
        customerTypeOptions={customerTypeOptions}
        handleSelectChange={handleSelectChange}
        handleSubmit={
          currentCustomer && currentCustomer.id
            ? handleUpdateCustomer
            : handleCreateCustomer
        }
      />
    </div>
  );
};

export default CustomerPage;
