import React, { useState } from 'react';
import { Text } from '@consta/uikit/Text';
import { Button } from '@consta/uikit/Button';
import AddCustomerModal from './AddCustomerModal';
import CustomerList from './CustomerList';

type Customer = {
  id: number;
  customer_code: string;
  customer_name: string;
  customer_inn: string;
  customer_kpp: string;
  customer_legal_address: string;
  customer_postal_address: string;
  customer_email: string;
  customer_code_main: string;
  is_organization: boolean;
  is_person: boolean;
};

const initialCustomers: Customer[] = [
  {
    id: 1,
    customer_code: '001',
    customer_name: 'ООО "Ромашка"',
    customer_inn: '1234567890',
    customer_kpp: '987654321',
    customer_legal_address: 'г. Москва, ул. Ленина, д. 1',
    customer_postal_address: 'г. Москва, ул. Ленина, д. 1',
    customer_email: 'info@romashka.ru',
    customer_code_main: '000',
    is_organization: true,
    is_person: false,
  },
  {
    id: 2,
    customer_code: '002',
    customer_name: 'ИП Иванов Иван Иванович',
    customer_inn: '2345678901',
    customer_kpp: '876543210',
    customer_legal_address: 'г. Санкт-Петербург, ул. Пушкина, д. 2',
    customer_postal_address: 'г. Санкт-Петербург, ул. Пушкина, д. 2',
    customer_email: 'ivanov@ip.ru',
    customer_code_main: '000',
    is_organization: false,
    is_person: true,
  },
  // Добавьте больше фейковых данных, если необходимо
];

function Customer() {
  const [isModalOpen, setIsModalOpen] = useState<boolean>(false);
  const [customers, setCustomers] = useState<Customer[]>(initialCustomers);
  const [editingCustomer, setEditingCustomer] = useState<Customer | null>(null);

  const handleAddCustomer = (customer: Customer) => {
    setCustomers([...customers, { ...customer, id: Date.now() }]);
  };

  const handleDeleteCustomer = (id: number) => {
    setCustomers(customers.filter((customer) => customer.id !== id));
  };

  const handleEditCustomer = (customer: Customer) => {
    setEditingCustomer(customer);
    setIsModalOpen(true);
  };

  const handleSaveCustomer = (updatedCustomer: Customer) => {
    setCustomers(
      customers.map((customer) =>
        customer.id === updatedCustomer.id ? updatedCustomer : customer
      )
    );
    setEditingCustomer(null);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setEditingCustomer(null);
  };

  return (
    <div className="container">
      <div className="instrument-header">
        <div>
          <Text as="h1">Управление заказчиками</Text>
          <Button label="Добавление" onClick={() => setIsModalOpen(true)} />
        </div>
      </div>

      <Text as="p" lineHeight="m">
        В этом окне вы можете управлять заказчиками, добавлять и удалять их, а
        также вносить необходимые изменения. Благодаря удобному интерфейсу,
        процесс управления клиентами является максимально простым и эффективным.
        При необходимости вы всегда сможете быстро обновить информацию,
        обеспечивая точность и актуальность данных.
      </Text>

      <hr />

      <CustomerList
        customers={customers}
        onDelete={handleDeleteCustomer}
        onEdit={handleEditCustomer}
      />

      <AddCustomerModal
        isOpen={isModalOpen}
        onClose={handleCloseModal}
        customer={editingCustomer}
        onSave={handleSaveCustomer}
      />
    </div>
  );
}

export default Customer;
