import React, { useState, useEffect } from 'react';
import { PurchaseApi } from '../../http/PurchaseApi';
import CustomerForm from './CustomerForm';

interface Customer {
  customer_id: number;
  customer_code: string;
  customer_name: string;
  customer_inn: string;
  customer_kpp: string;
  customer_legal_address: string;
  customer_postal_address: string;
  customer_email: string;
  customer_code_main: string;
  is_organization: boolean;
  is_person: boolean;
}

const CustomerPage: React.FC = () => {
  const [customers, setCustomers] = useState<Customer[]>([]);
  const [currentCustomer, setCurrentCustomer] = useState<Customer | null>(null);
  const [isEditing, setIsEditing] = useState<boolean>(false);

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = async () => {
    try {
      const response = await PurchaseApi.getCustomers();
      setCustomers(response.data);
    } catch (error) {
      console.error('Error fetching customers', error);
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    if (currentCustomer) {
      setCurrentCustomer({ ...currentCustomer, [name]: value });
    }
  };

  const handleCreateCustomer = async () => {
    if (currentCustomer) {
      try {
        await PurchaseApi.createCustomer(currentCustomer);
        fetchCustomers();
      } catch (error) {
        console.error('Error creating customer', error);
      }
    }
  };

  const handleUpdateCustomer = async () => {
    if (currentCustomer && currentCustomer.customer_id) {
      try {
        await PurchaseApi.updateCustomer(
          currentCustomer.customer_id,
          currentCustomer
        );
        fetchCustomers();
      } catch (error) {
        console.error('Error updating customer', error);
      }
    }
  };

  const handleDeleteCustomer = async (customerId: number) => {
    try {
      await PurchaseApi.deleteCustomer(customerId);
      fetchCustomers();
    } catch (error) {
      console.error('Error deleting customer', error);
    }
  };

  const handleEditCustomer = (customer: Customer) => {
    setCurrentCustomer(customer);
    setIsEditing(true);
  };

  const clearForm = () => {
    setCurrentCustomer(null);
    setIsEditing(false);
  };

  return (
    <div>
      <CustomerForm customers={customers} />
    </div>
  );
};

export default CustomerPage;
