import React from 'react';
import {
  Table,
  TableColumn,
  TableRow as ConstaTableRow,
} from '@consta/uikit/Table';
import { Button } from '@consta/uikit/Button';
import { Lot } from '../../intefaces/LotInterface';
import { format } from 'date-fns';
import { ru } from 'date-fns/locale';

interface LotTableRow extends ConstaTableRow {
  id: string;
  lotName: string;
  customerCode: string;
  price: number;
  currencyCode: string;
  ndsRate: string;
  placeDelivery: string;
  dateDelivery: string; // Изменено на string
}

interface Props {
  lots: Lot[];
}

const LotList: React.FC<Props> = ({ lots }) => {
  const columns: TableColumn<LotTableRow>[] = [
    {
      title: 'Наименование',
      accessor: 'lotName',
      width: 150,
    },
    {
      title: 'Контрагент',
      accessor: 'customerCode',
    },
    {
      title: 'Цена',
      accessor: 'price',
    },
    {
      title: 'Валюта',
      accessor: 'currencyCode',
    },
    {
      title: 'НДС',
      accessor: 'ndsRate',
    },
    {
      title: 'Место доставки',
      accessor: 'placeDelivery',
    },
    {
      title: 'Дата доставки',
      accessor: 'dateDelivery',
    },
  ];

  // Функция для форматирования даты с использованием date-fns
  const formatDate = (date: Date) => {
    return format(date, 'dd.MM.yyyy HH:mm', { locale: ru });
  };

  const rows: LotTableRow[] = lots.map((lot) => ({
    id: lot.id.toString(),
    lotName: lot.lotName,
    customerCode: lot.customerCode,
    price: lot.price,
    currencyCode: lot.currencyCode,
    ndsRate: lot.ndsRate,
    placeDelivery: lot.placeDelivery,
    dateDelivery: formatDate(new Date(lot.dateDelivery)), // Форматируем дату
  }));

  return <Table columns={columns} rows={rows} />;
};

export default LotList;
