import React from 'react';
import { Button } from '@consta/uikit/Button';
import { IconEdit } from '@consta/icons/IconEdit';
import { IconTrash } from '@consta/icons/IconTrash';
import { Table, TableColumn, TableRow } from '@consta/uikit/Table';

interface Customer {
  customer_id: number;
  customer_code: string;
  customer_name: string;
  customer_inn: string;
  customer_kpp: string;
  customer_legal_address: string;
  customer_postal_address: string;
  customer_email: string;
  customer_code_main: string;
  is_organization: boolean;
  is_person: boolean;
}

interface CustomerRow extends Customer, TableRow {
  id: string;
}

interface Props {
  customers: Customer[];
}

const columns: TableColumn<CustomerRow>[] = [
  {
    title: 'Код',
    accessor: 'customer_code',
  },
  {
    title: 'Наименование',
    accessor: 'customer_name',
  },
  {
    title: 'ИНН',
    accessor: 'customer_inn',
  },
  {
    title: 'КПП',
    accessor: 'customer_kpp',
  },
  {
    title: 'Юр. адрес',
    accessor: 'customer_legal_address',
  },
  {
    title: 'Почтовый адрес',
    accessor: 'customer_postal_address',
  },
  {
    title: 'Электронная почта',
    accessor: 'customer_email',
  },
  {
    title: 'Действия',
    renderCell: (customer: CustomerRow) => (
      <>
        <Button
          iconLeft={IconEdit}
          size="s"
          view="ghost"
          onClick={() => console.log(`Изменить ${customer.customer_code}`)}
          style={{ marginRight: '8px' }}
        />
        <Button
          iconLeft={IconTrash}
          size="s"
          view="ghost"
          onClick={() => console.log(`Удалить ${customer.customer_code}`)}
        />
      </>
    ),
  } as any,
];

const CustomerList: React.FC<Props> = ({ customers }) => {
  const rows: CustomerRow[] = customers.map((customer) => ({
    ...customer,
    id: customer.customer_id.toString(),
  }));

  return (
    <div>
      <Table columns={columns} rows={rows} />
    </div>
  );
};

export default CustomerList;
